// Get the HTML from previous node
const html = $input.all()[0].json.data;
const maxPages = $('When Executed by Another Workflow').first().json.MAX_PAGES;
const rateLimitSec = $('When Executed by Another Workflow').first().json.RATE_LIMIT_SEC;

// Simple regex to find article links
// Ynet articles typically follow pattern: /article/xxxxx
const articleRegex = /href=["']([^"']*\/article\/[^"']+)["']/gi;
const links = new Set();

let match;
while ((match = articleRegex.exec(html)) !== null) {
  let url = match[1];

  // Make absolute URL if relative
  if (url.startsWith('/')) {
    url = 'https://www.ynet.co.il' + url;
  }

  // Clean up URL (remove fragments, queries)
  url = url.split('#')[0].split('?')[0];

  links.add(url);

  // Stop if we have enough
  if (links.size >= maxPages) break;
}

// Convert to array and add jittered delays
const articleList = Array.from(links).slice(0, maxPages).map((url, index) => {
  // Add random jitter to delays (±300ms)
  const jitter = Math.floor(Math.random() * 600) - 300;
  const delay = rateLimitSec + jitter;

  return {
    article_url: url,
    delay_ms: Math.max(500, delay), // minimum 500ms
    index: index + 1
  };
});

// n8n requires this format
return articleList.map(item => ({ json: item }));

