const currentAd = $input.item.json;
// Get existing ads from the node that ran earlier
const existingAdsNode = $('Read All Existing Ads');
const existingAds = existingAdsNode ? existingAdsNode.all() : [];

// Find if this ad exists
const existingAd = existingAds.find(item => 
  item.json && item.json.image_hash === currentAd.image_hash
);

if (existingAd) {
  // Exists - keep original first_seen
  return {
    json: {
      ...currentAd,
      first_seen: existingAd.json.first_seen,
      exists: true,
      row_number: existingAd.json.row_number || null
    }
  };
} else {
  // New ad
  return {
    json: {
      ...currentAd,
      exists: false
    }
  };
}

const items = $input.all();
const hosts = $('When Executed by Another Workflow').first().json.AD_HOSTS;

console.log('=== FILTERING BY AD_HOSTS ===');
console.log('AD_HOSTS:', hosts);
console.log('Processing items:', items.length);

const allFoundAds = [];

for (const item of items) {
  const itemData = item.json;
  const pageUrl = itemData.article_url || 'unknown';

  console.log(`\nPage: ${pageUrl}`);
  console.log(`Images found: ${itemData.all_images?.length || 0}`);
  console.log(`Iframes found: ${itemData.all_iframes?.length || 0}`);

  // Process images
  if (itemData.all_images && Array.isArray(itemData.all_images)) {
    for (let idx = 0; idx < itemData.all_images.length; idx++) {
      const imgSrc = itemData.all_images[idx];
      if (!imgSrc) continue;

      const matched = hosts.find(h => imgSrc.toLowerCase().includes(h.toLowerCase()));

      if (matched) {
        console.log(`  ✓ Image match! ${matched} in ${imgSrc.substring(0, 60)}...`);

        const imgClass = itemData.all_images_class?.[idx] || '';

        allFoundAds.push({
          image_url: imgSrc.startsWith('http') ? imgSrc : (imgSrc.startsWith('//') ? 'https:' + imgSrc : 'https://www.ynet.co.il' + imgSrc),
          width: null,
          height: null,
          placement_hint: imgClass ? 'img:' + imgClass.split(' ')[0] : 'img-tag',
          ad_host: matched,
          page_url: pageUrl
        });
      }
    }
  }

  // Process iframes
  if (itemData.all_iframes && Array.isArray(itemData.all_iframes)) {
    for (const iframeSrc of itemData.all_iframes) {
      if (!iframeSrc) continue;

      const matched = hosts.find(h => iframeSrc.toLowerCase().includes(h.toLowerCase()));

      if (matched) {
        console.log(`  ✓ Iframe match! ${matched} in ${iframeSrc.substring(0, 60)}...`);

        allFoundAds.push({
          image_url: iframeSrc.startsWith('http') ? iframeSrc : (iframeSrc.startsWith('//') ? 'https:' + iframeSrc : iframeSrc),
          width: null,
          height: null,
          placement_hint: 'iframe:' + matched,
          ad_host: matched,
          page_url: pageUrl
        });
      }
    }
  }
}

console.log('\n=== RESULTS ===');
console.log('Total ads found:', allFoundAds.length);

return allFoundAds.map(ad => ({ json: ad }));

const items = $input.all();

// Filter only new ads (exists: false)
